// index.js
import Link from "next/link";
import groq from "groq";
import client from "../client";

import React from "react";
// import "fullpage.js/vend ors/scrolloverflow"; // Optional. When using scrollOverflow:true
import ReactFullpage from "@fullpage/react-fullpage";

const Index = (props) => {
  console.log(props);
  const { posts = [] } = props;
  const { projects = [] } = props;

  const postLinks = posts.map(
    ({ _id, title = "", slug = "", _updatedAt = "" }) =>
      slug && (
        <li key={_id}>
          <Link href="/post/[slug]" as={`/post/${slug.current}`}>
            <a>{title}</a>
          </Link>{" "}
          ({new Date(_updatedAt).toDateString()})
        </li>
      )
  );

  const projectLinks = projects.map(
    ({ _id, title = "", slug = "", _updatedAt = "" }) =>
      slug && (
        <li key={_id}>
          <Link href="/project/[slug]" as={`/project/${slug.current}`}>
            <a>{title}</a>
          </Link>{" "}
          ({new Date(_updatedAt).toDateString()})
        </li>
      )
  );

  if (window !== undefined) {
    return null;
  }

  return (
    <ReactFullpage
      // scrollOverflow={true}
      sectionsColor={["orange", "purple", "green"]}
      // onLeave={this.onLeave.bind(this)}
      // afterLoad={this.afterLoad.bind(this)}
      render={({ state, fullpageApi }) => {
        return (
          <div id="fullpage-wrapper">
            <div className="section section1">
              <h3>Section 1</h3>
            </div>
            <div className="section">
              <div className="slide">
                <h3>Slide 2.1</h3>
              </div>
              <div className="slide">
                <h3>Slide 2.2</h3>
              </div>
              <div className="slide">
                <h3>Slide 2.3</h3>
              </div>
            </div>
            <div className="section">
              <h3>Section 3</h3>
              <button onClick={() => fullpageApi.moveTo(1, 0)}>Move top</button>
            </div>
          </div>
        );
      }}
    />
  );
};

Index.getInitialProps = async () => ({
  posts: await client.fetch(groq`
      *[_type == "post" && publishedAt < now()]|order(publishedAt desc)
    `),
  projects: await client.fetch(groq`
      *[_type == "project" && publishedAt < now()]|order(publishedAt desc)
    `),
});

export default Index;
